import ShelfChanger from "./ShelfChanger";

const Search = ({ booksData, onsetShowPage }) => {
  return (
    <div className="search-books">
      <div className="search-books-bar">
        <a className="close-search" onClick={() => onsetShowPage()}>
          Close
        </a>
        <div className="search-books-input-wrapper">
          <input type="text" placeholder="Search by title, author, or ISBN" />
        </div>
      </div>
      <div className="search-books-results">
        <ol className="books-grid">
          {booksData.map((bookentry) => {
            return (
              <li key={bookentry.id}>
                <div className="book">
                  <div className="book-top">
                    <div
                      className="book-cover"
                      style={{
                        width: 128,
                        height: 193,
                        backgroundImage: `url(${bookentry.imageLinks.thumbnail})`,
                      }}
                    ></div>
                    <ShelfChanger />
                  </div>
                  <div className="book-title">{bookentry.title}</div>
                  <div className="book-authors">{bookentry.authors}</div>
                </div>
              </li>
            );
          })}
        </ol>
      </div>
    </div>
  );
};

export default Search;
