import { useState } from "react";

const ShelfChanger = ({ bookentry, onUpdateBooksData }) => {
  console.log(bookentry.shelf);
  const [shelf, setShelf] = useState(bookentry.shelf);

  const updateShelf = (event) => {
    onUpdateBooksData(bookentry, event.target.value);
    setShelf(event.target.value);
  };

  console.log(shelf);

  return (
    <div className="book-shelf-changer">
      <select value={shelf} onChange={(e) => updateShelf(e)}>
        <option value="none" disabled>
          Move to...
        </option>
        <option value="currentlyReading">Currently Reading</option>
        <option value="wantToRead">Want to Read</option>
        <option value="read">Read</option>
        <option value="none">None</option>
      </select>
    </div>
  );
};

export default ShelfChanger;
